	public static int getIndice(float[] value) {
		int retorno = -1;
		String data = Math.round(value[0]) + "" + Math.round(value[1]) + "" + Math.round(value[2]) + "" + Math.round(value[3]) + "" + Math.round(value[4]) + "" + Math.round(value[5]) + "" + Math.round(value[6]) + "" + Math.round(value[7]) + "" + Math.round(value[8]) + "" + Math.round(value[9]);
		System.out.println(data);
		switch(data) {
			case "1000000000":
				retorno = 0;
				break;
			case "0100000000":
				retorno = 1;
				break;				
			case "0010000000":
				retorno = 2;
				break;				
			case "0001000000":
				retorno = 3;
				break;				
			case "0000100000":
				retorno = 4;
				break;				
			case "0000010000":
				retorno = 5;
				break;				
			case "0000001000":
				retorno = 6;
				break;				
			case "0000000100":
				retorno = 7;
				break;				
			case "0000000010":
				retorno = 8;
				break;				
			case "0000000001":
				retorno = 9;
				break;				
			default:
				System.out.println("Este não é um imagem válido!");	
		}
		
		return retorno;
	}
